import Ecole from './Carte-ecole';
import Ecoleimg from '../img/ecole.png'
import styled from 'styled-components';

import { useSelector } from 'react-redux';
const FormationsCardsSection = ({formations}) => {


    let formation = useSelector(state=>state.formation)

    return (

        <Wrapper>
            {formation.data.length > 0 &&
            <>

            {formation.data.map((form)=> <Ecole className="box" img={Ecoleimg} titre={form.fields.g_ea_lib_vx} diplomes={form.fields.form_lib_voe_acc
} link="/details" />)}
            </>
            }

        </Wrapper>
    );
};

const Wrapper = styled.section`
padding-top:15rem;
padding-right:10rem;
padding-left:10rem;
padding-bottom:15rem;
display:flex;
justify-content:space-evenly;
 flex-direction:row;
 flex-wrap:wrap;
 `
export default FormationsCardsSection
