import React from 'react';
import { useSelector } from 'react-redux';
import FormationsCardsSection from '../components/FormationsCardsSection'
import styled from 'styled-components';

import Ecoleimg from '../img/ecole.png'


import Ecole from '../components/Carte-ecole';


const Formations = () => {

    let formation = useSelector(state=>state.formation)
    return (
        <div>


            <SearchbarSection className='searchSection'><input type="text" /></SearchbarSection>

            {formation.data.length > 0 &&
            <>
            {formation.data.filter((item , idx )=>idx<200).map(form=>( <Ecole className="box" img={Ecoleimg} titre={form.fields.g_ea_lib_vx} diplomes={form.fields.form_lib_voe_acc
        } link="/details" />))}</>
}


            <FormationsCardsSection />
        </div>
    );
};


let SearchbarSection = styled.div`

    margin-top: 8rem;
    width: 100%;

    display: flex;

    justify-content: center;
    align-items: center;
    input {
        width: 70%;
        border: none;

        font-size: 2rem;
        padding-bottom: 1rem;
        border-bottom: 2px solid black;

        border-radius: 2px;

        outline: none;
    }
`


export default Formations;
