import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import FormationsCardsSection from '../components/FormationsCardsSection'
import styled from 'styled-components';

import Ecoleimg from '../img/ecole.png'
import { useEffect } from 'react';


import { useDispatch } from 'react-redux';

import Ecole from '../components/Carte-ecole';
import { getFormations } from '../redux/reducers/formation';




const Formations = () => {
    let dispatch = useDispatch()

    let [showSection , setShowsection] = useState(false)

let [formations , setformations] = useState({data:[] , isSuccess:true,message:"",loading:false})

let formati = useSelector(state => state.formations)


    useEffect(() => {

        setformations(formati.data)
        dispatch(getFormations(80));
      }, []);



    const handleSearch = (e)=>{
        console.log(formations)

        setShowsection(true)
        setformations(formati.data.filter(form=>{return (form.fields.g_ea_lib_vx.includes(e.target.value) || form.fields.form_lib_voe_acc.includes(e.target.value))}))

        console.log(formations)


    }




    return (




        <div>


            <SearchbarSection className='searchSection'><input type="text" onChange={handleSearch}/></SearchbarSection>

           {showSection && 
           <GridSection className='grid'>
           {formations.map((form, i)=><Ecole key={i} side={sideHandle(i)} className="box" img={Ecoleimg} titre={form.fields.g_ea_lib_vx} diplomes={form.fields.form_lib_voe_acc } link="/details" />)}
           </GridSection>

           }


            <FormationsCardsSection />
        </div>
    );
};


let SearchbarSection = styled.div`

    margin-top: 8rem;
    width: 100%;

    display: flex;

    justify-content: center;
    align-items: center;
    input {
        width: 70%;
        border: none;

        font-size: 2rem;
        padding-bottom: 1rem;
        border-bottom: 2px solid black;

        border-radius: 2px;

        outline: none;
    }
`
let GridSection = styled.div`

padding-top:7rem;
padding-right:10rem;
padding-left:10rem;
padding-bottom:15rem;
display:flex;
justify-content:space-evenly;
 flex-direction:row;
 flex-wrap:wrap;


`

export default Formations;
