import React from 'react';
import { useSelector } from 'react-redux';
import FormationsCardsSection from '../components/FormationsCardsSection'
import styled from 'styled-components';

import Ecoleimg from '../img/ecole.png'
import { useEffect } from 'react';


import { useDispatch } from 'react-redux';

import Ecole from '../components/Carte-ecole';
import { getFormations } from '../redux/reducers/formation';

const Formations = () => {

    useEffect(() => {
        dispatch(getFormations());
      }, []);


    return (




        <div>


            <SearchbarSection className='searchSection'><input type="text" /></SearchbarSection>




            <FormationsCardsSection />
        </div>
    );
};


let SearchbarSection = styled.div`

    margin-top: 8rem;
    width: 100%;

    display: flex;

    justify-content: center;
    align-items: center;
    input {
        width: 70%;
        border: none;

        font-size: 2rem;
        padding-bottom: 1rem;
        border-bottom: 2px solid black;

        border-radius: 2px;

        outline: none;
    }
`


export default Formations;
