  const item = {
    hidden: { opacity: 0 },
    show: { opacity: 1 }
  }


const Ecole= ({img, titre, diplomes, link}) => (
        <Wrapper initial="hidden" animate="show" variants={item}>

            <img src={img}></img>
            <H2>{titre}</H2>
            <H3>{diplomes}</H3>
            <Stars>
                <img src={star}></img>
                <img src={star}></img>
                <img src={star}></img>
                <img src={starempty}></img>
                <img src={starempty}></img>
            </Stars>
            <a href={link}>DÃ‰TAILS DE LA FORMATION</a>
        </Wrapper>
);

const Wrapper=styled(motion.section)`

   /* @keyframes fadein {
