    opacity: 1;



    animation: fadein 1.5s;
    margin-top:3rem;
    max-height: 100%;
    display: flex;


   justify-content:space-between;
    flex-direction : column ; 
    box-shadow: 0px 12px 20px 0px rgba(0, 0, 0, 0.25);
    width:30%;
    border-radius:10px 10px 10px 10px;
    img{
    width:100%;
        border-radius:10px 10px 0px 0px;
    }
    a{
        color:white;
        font-weight:700;
        font-size:20px;
        text-decoration:none;
        background-color:#E54B4B;
        display:flex;
        justify-content:center;

        align-items : center;

        height : 3.5rem ;
        border-radius:0px 0px 10px 10px;
    }
`
const H2=styled.h2`
    color:#344055;
    padding-top:10px;
    padding-bottom:20px;
    padding-left:20px;
    font-weight:700;
    font-size:20px;
`
const H3=styled.h3`
    padding-bottom:20px;
    padding-left:20px;
    color:#344055;
    font-weight:400;
    font-size:20px;
`
const Stars=styled.div`
    padding-left:20px;
    padding-bottom:20px;
    img{
        height:20px;
        width:20px;
    }
`

export default Ecole
