import Ecole from './Carte-ecole';
import Ecoleimg from '../img/ecole.png'
import styled from 'styled-components';
import {motion} from 'framer-motion'
import { useSelector } from 'react-redux';
const FormationsCardsSection = () => {

    const container = {
        hidden: { opacity: 0 },
        show: {
          opacity: 1,
          transition: {
            delayChildren: "1s",
            staggerDirection: -1
          }
        }
      }

    const formationslimited = useSelector((state) => state.formationLimited);
    return (

        <Wrapper> 
            {formationslimited.data.length > 0 &&


            formationslimited.data.map((form)=> <Ecole className="box" img={Ecoleimg} titre={form.fields.g_ea_lib_vx} diplomes={form.fields.form_lib_voe_acc
} link="/details" />)

            }

        </Wrapper>
    );
};

const Wrapper = styled(motion.section)`
padding-top:7rem;
padding-right:10rem;
padding-left:10rem;
padding-bottom:15rem;
display:flex;
justify-content:space-evenly;
 flex-direction:row;
 flex-wrap:wrap;
 `
export default FormationsCardsSection
