import React, { useState } from "react";
import { useSelector } from "react-redux";
import FormationsCardsSection from "../components/FormationsCardsSection";
import styled from "styled-components";

import Ecoleimg from "../img/ecole.png";
import { useEffect } from "react";
import { motion } from "framer-motion";
import { useDispatch } from "react-redux";

import Ecole from "../components/Carte-ecole";
import { getFormations } from "../redux/reducers/formation";

const Formations = () => {
  let dispatch = useDispatch();

  let [showSection, setShowsection] = useState(false);

  let [formations, setformations] = useState({
    data: [],
    isSuccess: true,
    message: "",
    loading: false,
  });

  let formati = useSelector((state) => state.formations);

  useEffect(() => {
    setformations(formati.data);
    dispatch(getFormations(80));
  }, []);

  const handleSearch = (e) => {
    console.log(formations);

    setShowsection(true);
    setformations(
      formati.data.filter((form) => {
        return (
          form.fields.g_ea_lib_vx.includes(e.target.value) ||
          form.fields.form_lib_voe_acc.includes(e.target.value)
        );
      })
    );

    console.log(formations);
  };

  const sideHandle = (i) => {
    if (i % 3 == 0) {
      return "right";
    } else if (i % 3 == 1) {
      return "center";
    } else if (i % 3 == 2) {
      return "left";
    }
  };

  return (
    <div>
      <SearchbarSection className="searchSection">
        <input type="text" onChange={handleSearch} />
      </SearchbarSection>

      {showSection && (
        <>
          <StyledH2 formations={formations.length > 0 ? "full" : "empty"}>
            <h2>Resultats de recherche</h2>
          </StyledH2>
          <GridSection className="grid">
            {formations.map((form, i) => (
              <Ecole
                key={i}
                side={sideHandle(i)}
                className="box"
                img={Ecoleimg}
                titre={form.fields.g_ea_lib_vx}
                diplomes={form.fields.form_lib_voe_acc}
                link="/details"
              />
            ))}
          </GridSection>
        </>
      )}

      <FormationsCardsSection />
    </div>
  );
};

let SearchbarSection = styled.div`
  margin-top: 8rem;
  width: 100%;

  display: flex;

  justify-content: center;
  align-items: center;

  h2 {
    margin-left: 10rem;

    width: 100%;
  }
  input {
    width: 70%;
    border: none;

    font-size: 2rem;
    padding-bottom: 1rem;
    border-bottom: 2px solid black;

    border-radius: 2px;

    outline: none;
  }
`;

let StyledH2 = styled(motion.div)`
  color: ${(props) => (props.formations == "full" ? "palevioletred" : "red")};
  margin-top: 5rem;
  display: flex;

  align-items: center;
  width: 100%;
  justify-content: center;

  h2 {
    display: flex;
    width: 77%;
    align-items: flex-start;
    transition: all 1s;

    margin-right: 0%;
    justify-content: ${(props) =>
      props.formations == "full" ? "flex-start" : "center"};
  }
`;
let GridSection = styled.div`
  padding-top: 5rem;
  padding-right: 10rem;
  padding-left: 10rem;
  padding-bottom: 15rem;
  display: flex;
  justify-content: space-evenly;
  flex-direction: row;
  flex-wrap: wrap;
`;

export default Formations;
