import { createSlice,createAsyncThunk} from '@reduxjs/toolkit'
import axios from "axios"
const initialState = { data:[] , isSuccess : false , message:"" , loading:false }

const getFormations = createAsyncThunk(
    "get/formationsData" , 
    async (arg , {rejectWithValue})=>{
        try {
            const {data} = await axios.get('localhost:8000/ecoles/63aec283b835e6f33eb88195')
        } catch (error) {
            rejectWithValue(error.response.data)
        }
    }
)

const formationSlice = createSlice({
  name: 'formation',
  initialState,
  reducers: {

  },

  extraReducers : {
    [getFormations.loading]:(state , {payload})=>{
        state.message = payload , 
        state.loading = true
        state.message = payload
    }
}
})



export default formationSlice
